import {
  FlatList,
  Image,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import React from "react";
import { useNavigation } from "@react-navigation/native";
import Icons from "@expo/vector-icons/MaterialIcons";
import Header from "../Header";
const Home = () => {
  const navigation = useNavigation();

  const handleInputChange = () => {};
  const data = [
    {
      id: 1,
      icon: "check-circle-outline",
      title: "Active Organaization",
      value: "12,345",
      valueIndicatorUp: "arrow-drop-up",
      valueIndicatordown: "arrow-drop-down",
      style: {
        backgroundColor: "rgba(22, 210, 41, 0.3)",
      },
      color: "rgba(22, 210, 41,1)",
    },
    {
      id: 2,
      icon: "control-point",
      title: "New Added Organaization",
      value: "12,345",
      valueIndicatorUp: "arrow-drop-up",
      valueIndicatordown: "arrow-drop-down",
      style: {
        backgroundColor: "rgba(0, 178, 255, 0.3)",
      },
      color: "rgba(0, 178, 255,1)",
    },
    {
      id: 3,
      icon: "sync",
      title: "License Updated",
      value: "12,345",
      valueIndicatorUp: "arrow-drop-up",
      valueIndicatordown: "arrow-drop-down",
      style: {
        backgroundColor: "rgba(235, 255, 0, 0.3)",
      },
      color: "#FF9900",
    },
    {
      id: 4,
      icon: "close",
      title: "License Removed",
      value: "12,345",
      valueIndicatorUp: "arrow-drop-up",
      valueIndicatordown: "arrow-drop-down",
      style: {
        backgroundColor: "rgba(255, 37, 37, 0.3)",
      },
      color: "rgba(255, 37, 37,1)",
    },
  ];
  return (
    <View style={styles.container}>
      <Header />
      <View style={{ flex: 1, width: "100%", alignItems: "center" }}>
        <View
          style={{
            flexDirection: "row",
            borderWidth: 1,
            justifyContent: "center",
            borderRadius: 30,
            borderColor: "#039EBD",
          }}
        >
          <TextInput
            style={{
              height: 40,
              width: "65%",
              padding: 10,
            }}
            onChangeText={handleInputChange}
            value={""}
            placeholder="Search"
            keyboardType="web-search"
          />
          <TouchableOpacity
            style={{
              width: "15%",
              height: 40,
              backgroundColor: "#039EBD",
              justifyContent: "center",
              alignItems: "center",
              borderTopEndRadius: 30,
              borderBottomEndRadius: 30,
              borderWidth: 0,
            }}
          >
            <Icons name="search" size={26} />
          </TouchableOpacity>
        </View>
      </View>
      <View style={{ flex: 4 }}>
        <FlatList
          data={data}
          ItemSeparatorComponent={() => {
            return <View style={{ height: 10 }}></View>;
          }}
          renderItem={({ item }) => (
            <View
              key={item.id}
              style={[
                item.style,
                {
                  flexDirection: "row",
                  justifyContent: "space-between",
                  marginHorizontal: 20,
                  paddingHorizontal: 18,
                  paddingVertical: 36,
                  borderRadius: 16,
                },
              ]}
            >
              <View
                style={{ flexDirection: "row", alignItems: "center", gap: 8 }}
              >
                <Icons
                  name={item.icon}
                  size={24}
                  style={{ color: item.color }}
                />
                <Text style={[styles.text, { fontWeight: 600 }]}>
                  {item.title}
                </Text>
              </View>
              <View
                style={{ flexDirection: "row", alignItems: "center", gap: 5 }}
              >
                <Text style={[styles.text]}>{item.value}</Text>
                <Icons name={item.valueIndicatorUp} size={24} />
              </View>
            </View>
          )}
          keyExtractor={(item) => item.id}
        />
      </View>
    </View>
  );
};

export default Home;

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: "white" },
  text: {
    fontSize: 16,
    fontWeight: "700",
  },
});
