import {
  FlatList,
  Image,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import React, { useEffect, useRef, useState } from "react";
import { useNavigation } from "@react-navigation/native";
import Icons from "@expo/vector-icons/MaterialIcons";
import Header from "../Header";
import Search from "../Search";
import axios from "axios";
import { encode as base64Encode } from "base-64";
import { data } from "../../assets/api/dummyResult";
const Home = () => {
  const navigation = useNavigation();
  const [typedText, setTypedText] = useState("");
  // const [result, setResult] = useState([]);
  const [suggestions, setSuggestions] = useState([]);

  const [isFocused, setIsFocused] = useState(false);

  const handleFocus = () => {
    console.log("focus");
    setIsFocused(true);
  };
  const handleBlur = () => {
    console.log("blur");
    setIsFocused(false);
  };

  const data = [
    {
      id: 1,
      icon: "check-circle-outline",
      title: "Active Organaization",
      value: "12,345",
      valueIndicatorUp: "arrow-drop-up",
      valueIndicatordown: "arrow-drop-down",
      style: {
        backgroundColor: "rgba(22, 210, 41, 0.3)",
      },
      color: "rgba(22, 210, 41,1)",
    },
    {
      id: 2,
      icon: "control-point",
      title: "New Added Organaization",
      value: "12,345",
      valueIndicatorUp: "arrow-drop-up",
      valueIndicatordown: "arrow-drop-down",
      style: {
        backgroundColor: "rgba(0, 178, 255, 0.3)",
      },
      color: "rgba(0, 178, 255,1)",
    },
    {
      id: 3,
      icon: "sync",
      title: "License Updated",
      value: "12,345",
      valueIndicatorUp: "arrow-drop-up",
      valueIndicatordown: "arrow-drop-down",
      style: {
        backgroundColor: "rgba(235, 255, 0, 0.3)",
      },
      color: "#FF9900",
    },
    {
      id: 4,
      icon: "close",
      title: "License Removed",
      value: "12,345",
      valueIndicatorUp: "arrow-drop-up",
      valueIndicatordown: "arrow-drop-down",
      style: {
        backgroundColor: "rgba(255, 37, 37, 0.3)",
      },
      color: "rgba(255, 37, 37,1)",
    },
  ];
  const jsonData = [
    { id: 1, value: "Item 1" },
    { id: 2, value: "Item 2" },
    { id: 3, value: "Item 3" },
    { id: 4, value: "Item 4" },
    { id: 5, value: "Item 5" },
    { id: 6, value: "Item 6" },
  ];

  const handleTextChange = (text) => {
    // Filter suggestions based on user input
    const filteredSuggestions = jsonData
      .filter((item) => item.value.toLowerCase().includes(text.toLowerCase()))
      .slice(0, 4); // Limit to 5 suggestions
    setSuggestions(filteredSuggestions);
    setTypedText(text);
  };

  const handleItemSelect = (item) => {
    // Handle the selected item (e.g., navigate to a detail screen)
    console.log("Selected item:", item);
    setTypedText(item.value);
    // Additional logic as needed
  };

  const apiKey = "febd5655-5cc1-4c80-a9d8-f1c89005bb76";
  const companyId = "00000006";
  // const apiUrl = `https://api.company-information.service.gov.uk/company/${companyId}`;
  const apiUrl = `https://api.company-information.service.gov.uk/search/companies?q=${typedText}`;
  //KM Catering Limited

  const config = {
    headers: {
      Authorization: `Basic ${base64Encode(`${apiKey}:`)}`,
    },
  };
  const handleSubmit = () => {
    // axios
    //   .get(apiUrl, config)
    //   .then((response) => {
    //     console.log(response.data);
    //     setResult()
    //   })
    //   .catch((error) => {
    //     console.error(error.response.data);
    //   });
  };

  return (
    <View style={styles.container}>
      <Header />
      <View
        style={{
          flex: isFocused ? 3 : 1,
          width: "100%",
          alignItems: "center",
          flexDirection: "column",
          justifyContent: "flex-start",
        }}
      >
        <Search
          handleTextChange={handleTextChange}
          typedText={typedText}
          handleFocus={handleFocus}
          handleBlur={handleBlur}
          handlSubmt={handleSubmit}
        />
        {typedText.length !== 0 && isFocused && (
          <View
            style={{
              width: "100%",
              alignItems: "center",
              marginBottom: 45,
            }}
          >
            <View
              style={{
                width: "65%",
                backgroundColor: "#DDF3F8",
              }}
            >
              {suggestions.length !== 0 ? (
                <>
                  {suggestions.map((item) => (
                    <TouchableOpacity
                      key={item.id}
                      style={{
                        margin: 5,
                        borderBottomWidth: 1,
                        borderBottomColor: "#039EBD",
                        padding: 10,
                      }}
                      onPress={() => handleItemSelect(item)}
                    >
                      <Text style={{ textAlign: "center" }}>{item.value}</Text>
                    </TouchableOpacity>
                  ))}
                </>
              ) : (
                <View
                  style={{
                    width: "65%",
                    backgroundColor: "#DDF3F8",
                    padding: 10,
                  }}
                >
                  <Text style={{ textAlign: "center" }}>No result match.</Text>
                </View>
              )}
            </View>
          </View>
        )}
      </View>
      {!isFocused && (
        <View style={{ flex: 4 }}>
          <FlatList
            data={data}
            showsHorizontalScrollIndicator={false}
            showsVerticalScrollIndicator={false}
            renderItem={({ item }) => (
              <View
                key={item.id}
                style={[
                  item.style,
                  {
                    flexDirection: "row",
                    justifyContent: "space-between",
                    marginHorizontal: 20,
                    paddingHorizontal: 18,
                    paddingVertical: 36,
                    borderRadius: 16,
                    margin: 7,
                  },
                ]}
              >
                <View
                  style={{ flexDirection: "row", alignItems: "center", gap: 8 }}
                >
                  <Icons
                    name={item.icon}
                    size={24}
                    style={{ color: item.color }}
                  />
                  <Text style={[styles.text, { fontWeight: 600 }]}>
                    {item.title}
                  </Text>
                </View>
                <View
                  style={{ flexDirection: "row", alignItems: "center", gap: 5 }}
                >
                  <Text style={[styles.text]}>{item.value}</Text>
                  <Icons name={item.valueIndicatorUp} size={24} />
                </View>
              </View>
            )}
            keyExtractor={(item) => item.id}
          />
        </View>
      )}
    </View>
  );
};

export default Home;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
  },
  text: {
    fontSize: 16,
    fontWeight: "700",
  },
});
