import {
  Button,
  FlatList,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from "react-native";
import React, { useState } from "react";
import ProfileFormInput from "./ProfileFormInput";
import Search from "./Search";
import Loading from "./utils/Loading";
import { getSearchResult } from "../helper/getSearchResult";
import { companyHouseSearch } from "../helper/companyHouseSearch";
import { setUser } from "../slices/appSlice";
import { useDispatch } from "react-redux";
import AsyncStorage from "@react-native-async-storage/async-storage";

const ProfileMakingForm = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [company, setCompany] = useState("");
  const [visaExpiry, setVisaExpiry] = useState("");
  const [license_tier, setLicense_tier] = useState("");
  const [status, setStatus] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [notfound, setNotfound] = useState(false);
  const [errors, setErrors] = useState({});
  const [isFocused, setIsFocused] = useState(false);

  const dispatch = useDispatch();

  const handleFocus = () => {
    console.log("focus");
    setIsFocused(true);
  };
  const handleBlur = () => {
    console.log("blur");
    setIsFocused(false);
  };

  const handleCompanyChanges = (e) => {
    setCompany(e);
    if (company === "") {
      setAddress("");
      setVisaExpiry("");
      setLicense_tier("");
      setStatus("");
    }
  };
  const handleVisaExpiryChages = (inputDate) => {
    // Regex to match DD-MM-YYYY format
    const regex = /^(\d{1,2})-(\d{1,2})-(\d{4})$/;

    if (regex.test(inputDate)) {
      setVisaExpiry(inputDate);
    }
  };

  const handleSubmit = async () => {
    if (company !== "") {
      setLoading(true);
      const result = await getSearchResult(company);
      setLoading(false);

      if (result === null) {
        setCompany("");
        setSuggestions([]);
        setNotfound(true);
        setTimeout(() => {
          setNotfound(false);
        }, 2000);
      }

      if (company !== "" && result?.relatedResult === null) {
        setCompany(result?.company_name);
        setAddress(result?.address);
        setLicense_tier(result?.license_tier);
        setStatus(result?.status ? result.status : "Not found");
      }
      if (company !== "" && result?.relatedResult !== null) {
        setSuggestions(result?.relatedResult);
      }
    } else {
      setSuggestions([]);
    }
  };

  const handleSelection = async (item) => {
    setLoading(true);
    const result = await companyHouseSearch(item);
    setLoading(false);
    setSuggestions([]);
    setCompany(result?.company_name);
    setAddress(result?.address);
    setLicense_tier(result?.license_tier);
    setStatus(result?.status ? result.status : "Not found");
  };

  const validateForm = () => {
    const errors = {};

    if (!name.trim()) {
      errors.name = "Name is required";
    }

    if (!email.trim()) {
      errors.email = "Email is required";
    } else if (!isValidEmail(email)) {
      errors.email = "Invalid email format";
    }

    setErrors(errors);

    return Object.keys(errors).length === 0;
  };

  const isValidEmail = (email) => {
    // Very basic email validation
    // const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    // return emailRegex.test(email);
    return email;
  };

  // function to store user-info to local storage
  const storeData = async (value) => {
    try {
      const jsonValue = JSON.stringify(value);
      await AsyncStorage.setItem("user-info", jsonValue);
    } catch (e) {
      // saving error
    }
  };

  const handleSave = async () => {
    console.log("preesed");
    if (validateForm()) {
      // Do something with the form data
      try {
        setSaving(true);
        const jsonValue = {
          name,
          email,
          address,
          company,
          license_tier,
          status,
          visaExpiry,
        };
        await storeData(jsonValue);
        dispatch(setUser(jsonValue));
        console.log("done");

        setSaving(false);
      } catch (e) {
        console.log(e);
      }
    } else {
      console.log("Form has errors. Please check.");
    }
  };
  return (
    <ScrollView
      // showsHorizontalScrollIndicator={false}
      // showsVerticalScrollIndicator={false}
      keyboardShouldPersistTaps="always"
    >
      <View style={{ paddingHorizontal: 32 }}>
        {/* --------- input ----------- */}
        <ProfileFormInput
          title={"Name"}
          require={"optional"}
          value={name}
          handleInputChange={setName}
          placeholder={"Enter your name"}
          keyboardType={"default"}
        />
        {errors.name && (
          <Text style={{ color: "red", marginLeft: 10 }}>{errors.name}</Text>
        )}
        {/* --------- input ----------- */}

        {/* --------- input ----------- */}
        <ProfileFormInput
          title={"Email"}
          require={"require"}
          value={email}
          handleInputChange={setEmail}
          placeholder={"example@mail.com"}
          keyboardType={"email-address"}
        />
        {errors.email && (
          <Text style={{ color: "red", marginLeft: 10 }}>{errors.email}</Text>
        )}
        {/* --------- input ----------- */}

        {/* --------- input ----------- */}
        <View style={{ position: "relative" }}>
          <ProfileFormInput
            title={"Company"}
            value={company}
            handleInputChange={handleCompanyChanges}
            placeholder={"Write you company name"}
            handleFocus={handleFocus}
            handleBlur={handleBlur}
            handleSubmit={handleSubmit}
            keyboardType={"default"}
          />
          {loading && (
            <View style={{ padding: 10 }}>
              <Loading />
            </View>
          )}
          {!loading && notfound && (
            <View
              style={{
                padding: 10,
              }}
            >
              <Text style={{ textAlign: "center" }}>
                No result found. Try something else.
              </Text>
            </View>
          )}
          {suggestions?.length > 0 && company !== "" && !loading && (
            <View
              style={{
                borderWidth: 1,
                borderRadius: 15,
                borderColor: "#039EBD",
                backgroundColor: "#cfe7ff",
              }}
            >
              {suggestions?.length !== 0 &&
                suggestions?.map((item, index) => (
                  <TouchableOpacity
                    key={index}
                    style={{
                      margin: 5,
                      borderBottomWidth: 1,
                      borderBottomColor: "#039EBD",
                      padding: 10,
                    }}
                    onPress={() => handleSelection(item)}
                  >
                    <Text style={{ textAlign: "center" }}>
                      {item["Organisation Name"]}
                    </Text>
                  </TouchableOpacity>
                ))}
            </View>
          )}
        </View>

        {errors.company && (
          <Text style={{ color: "red", marginLeft: 10 }}>{errors.company}</Text>
        )}
        {/* --------- input ----------- */}

        {/* --------- input ----------- */}
        {address !== "" && company !== "" && (
          <>
            <ProfileFormInput
              title={"Address"}
              value={address}
              handleInputChange={setAddress}
              placeholder={""}
              keyboardType={"default"}
            />
            {errors.address && (
              <Text style={{ color: "red", marginLeft: 10 }}>
                {errors.address}
              </Text>
            )}
          </>
        )}

        {/* --------- input ----------- */}

        {/* --------- input ----------- */}
        {address !== "" && company !== "" && (
          <>
            <ProfileFormInput
              title={"License tier"}
              value={license_tier}
              handleInputChange={setLicense_tier}
              placeholder={""}
              keyboardType={"default"}
            />
            {errors.license_tier && (
              <Text style={{ color: "red", marginLeft: 10 }}>
                {errors.license_tier}
              </Text>
            )}
          </>
        )}
        {/* --------- input ----------- */}

        {/* --------- input ----------- */}
        {address !== "" && company !== "" && (
          <>
            <ProfileFormInput
              title={"Status"}
              value={status}
              handleInputChange={setStatus}
              placeholder={""}
              keyboardType={"default"}
            />
            {errors.status && (
              <Text style={{ color: "red", marginLeft: 10 }}>
                {errors.status}
              </Text>
            )}
          </>
        )}
        {/* --------- input ----------- */}

        {/* --------- input ----------- */}
        {address !== "" && company !== "" && (
          <>
            <ProfileFormInput
              title={"Visa Expiry"}
              value={handleVisaExpiryChages}
              handleInputChange={setVisaExpiry}
              placeholder={"Enter date (DD-MM-YYYY)"}
              keyboardType={"numeric"}
            />
            {errors.visaExpiry && (
              <Text style={{ color: "red", marginLeft: 10 }}>
                {errors.visaExpiry}
              </Text>
            )}
          </>
        )}
        {/* --------- input ----------- */}

        {/* submit button start */}
        <View style={{ alignItems: "center" }}>
          <View
            style={{
              marginTop: 10,
              width: "45%",
            }}
          >
            <Button
              title={!saving ? "save" : "saving..."}
              color={"#039EBD"}
              onPress={handleSave}
            />
          </View>
        </View>
        {/* submit button end */}
      </View>
    </ScrollView>
  );
};

export default ProfileMakingForm;

const styles = StyleSheet.create({});
