import { Image, StyleSheet, Text, View } from "react-native";
import React from "react";

const NewsCard = () => {
  return (
    <View style={styles.card}>
      <View style={styles.textContainer}>
        <Text style={styles.title}>
          What the 'future histories' of the 1920s can teach us about hope
        </Text>
        <Text style={styles.subtitle}>
          Lessons from the 'flagrantly fictional' forecasts of a century ago...
        </Text>
      </View>
      <Image
        source={require("../assets/images/news.png")}
        style={styles.image}
      />
    </View>
  );
};

export default NewsCard;

const styles = StyleSheet.create({
  card: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    paddingHorizontal: 12,
    paddingVertical: 12,
    backgroundColor: "#F3F3F3",
    borderRadius: 13,
    shadowColor: "#000000",
    shadowOpacity: 0.1,
    shadowRadius: 13,
    shadowOffset: {
      width: 0,
      height: 2,
    },
    elevation: 2,
    marginBottom: 10,
  },
  textContainer: {
    flex: 1,
    marginRight: 8,
  },
  title: {
    fontSize: 16,
    fontWeight: "bold",
    marginBottom: 4,
  },
  subtitle: {
    fontSize: 14,
    color: "#666666",
  },
  image: {
    width: 100,
    height: 70,
    borderRadius: 10,
  },
});
